import { Button } from "@/components/ui/button";
import { useNavigate } from "react-router-dom";
import { useState } from "react";

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [rememberMe, setRememberMe] = useState(false);

  async function handleSignIn(e) {
    e?.preventDefault?.();
    setError("");
    setLoading(true);
    try {
      const res = await fetch("http://localhost:3000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });
      const payload = await res.json();
      if (!res.ok || payload?.error) {
        throw new Error(payload?.error?.message || "Login failed");
      }
      if (payload?.data?.token) {
        localStorage.setItem("authToken", payload.data.token);
      }
      if (payload?.data?.user) {
        localStorage.setItem("authUser", JSON.stringify(payload.data.user));
      }
      navigate("/test");
    } catch (err) {
      setError(err.message || "Login failed");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="min-h-screen flex">
      {/* Left Side - Image/Background */}
      <div className="hidden lg:flex lg:w-1/2 relative bg-gradient-to-br from-primary/20 via-accent/10 to-primary/30 overflow-hidden">
        <div className="absolute inset-0 bg-[url('https://images.unsplash.com/photo-1449824913935-59a10b8d2000?w=1200')] bg-cover bg-center opacity-40" />
        <div className="relative z-10 flex flex-col justify-center items-start p-16 text-foreground">
          <h1 className="text-5xl font-bold mb-4">
            Unified Traffic <br />Violation System
          </h1>
          <p className="text-xl text-muted-foreground max-w-md">
            Streamlining traffic enforcement and improving road safety across Local Government Units.
          </p>
        </div>
      </div>

      {/* Right Side - Login Form */}
      <div className="w-full lg:w-1/2 flex items-center justify-center p-8 bg-background">
        <div className="w-full max-w-md">
          {/* Header */}
          <div className="text-center mb-8">
            <button
              onClick={() => navigate("/")}
              className="inline-flex items-center gap-2 px-4 py-2 rounded-lg bg-muted text-muted-foreground hover:bg-muted/80 transition-colors mb-6 text-sm"
            >
              ‚Üê Back to Home
            </button>
            <h2 className="text-3xl font-bold mb-2">Login to your Account</h2>
            <p className="text-muted-foreground">Welcome back! Please enter your credentials</p>
          </div>

          {/* Login Form */}
          <form className="space-y-5" onSubmit={handleSignIn}>
            <div className="space-y-2">
              <label htmlFor="username" className="text-sm font-medium">
                Email or Username
              </label>
              <input
                id="username"
                type="text"
                className="w-full border border-border rounded-lg px-4 py-3 bg-background text-foreground focus:outline-none focus:ring-2 focus:ring-primary transition-all"
                placeholder="Enter your email or username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>

            <div className="space-y-2">
              <label htmlFor="password" className="text-sm font-medium">
                Password
              </label>
              <input
                id="password"
                type="password"
                className="w-full border border-border rounded-lg px-4 py-3 bg-background text-foreground focus:outline-none focus:ring-2 focus:ring-primary transition-all"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            {/* Remember Me & Forgot Password */}
            <div className="flex items-center justify-between text-sm">
              <label className="flex items-center gap-2 cursor-pointer">
                <input
                  type="checkbox"
                  checked={rememberMe}
                  onChange={(e) => setRememberMe(e.target.checked)}
                  className="w-4 h-4 rounded border-border text-primary focus:ring-2 focus:ring-primary"
                />
                <span className="text-muted-foreground">Remember Me</span>
              </label>
              <button
                type="button"
                className="text-primary hover:underline"
                onClick={() => alert("Forgot password feature coming soon!")}
              >
                Forgot Password?
              </button>
            </div>

            {/* Error Message */}
            {error && (
              <div className="p-3 rounded-lg bg-destructive/10 border border-destructive/20 text-destructive text-sm">
                {error}
              </div>
            )}

            {/* Login Button */}
            <Button
              type="submit"
              className="w-full py-6 text-base font-semibold"
              disabled={loading}
            >
              {loading ? "Signing In..." : "Login"}
            </Button>
          </form>

          {/* Divider */}
          <div className="relative my-8">
            <div className="absolute inset-0 flex items-center">
              <div className="w-full border-t border-border"></div>
            </div>
            <div className="relative flex justify-center text-sm">
              <span className="px-4 bg-background text-muted-foreground">
                Need help?
              </span>
            </div>
          </div>

          {/* Contact Admin */}
          <div className="text-center">
            <button
              onClick={() => navigate("/contact")}
              className="text-primary hover:underline text-sm font-medium"
            >
              Contact Administrator
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;